import React from 'react';
import {
  Box,
  TextField,
  Grid,
  Typography,
} from '@mui/material';
import type { HotelConfigFormData } from '../../../types/hotel';

interface BasicInfoStepProps {
  initialData: Partial<HotelConfigFormData>;
  onComplete: (data: Partial<HotelConfigFormData>) => void;
}

interface FormData {
  name: string;
  description: string;
  address: string;
  contactInfo: {
    phone: string;
    email: string;
    website: string;
  };
}

const BasicInfoStep: React.FC<BasicInfoStepProps> = ({ initialData, onComplete }) => {
  const [formData, setFormData] = React.useState<FormData>({
    name: initialData.name || '',
    description: initialData.description || '',
    address: initialData.address || '',
    contactInfo: {
      phone: initialData.contactInfo?.phone || '',
      email: initialData.contactInfo?.email || '',
      website: initialData.contactInfo?.website || '',
    },
  });

  const handleChange = (field: keyof FormData | 'contactInfo.phone' | 'contactInfo.email' | 'contactInfo.website', value: string) => {
    if (field.startsWith('contactInfo.')) {
      const contactField = field.split('.')[1] as keyof FormData['contactInfo'];
      setFormData(prev => ({
        ...prev,
        contactInfo: {
          ...prev.contactInfo,
          [contactField]: value,
        },
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        [field]: value,
      }));
    }
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onComplete(formData);
  };

  return (
    <Box component="form" onSubmit={handleSubmit}>
      <Typography variant="h6" gutterBottom>
        Basic Information
      </Typography>
      <Typography variant="body2" color="text.secondary" paragraph>
        Please provide the basic information about your hotel.
      </Typography>

      <Grid container spacing={3}>
        <Grid item xs={12}>
          <TextField
            required
            fullWidth
            label="Hotel Name"
            value={formData.name}
            onChange={(e) => handleChange('name', e.target.value)}
            error={!formData.name}
            helperText={!formData.name ? 'Hotel name is required' : ''}
          />
        </Grid>

        <Grid item xs={12}>
          <TextField
            fullWidth
            multiline
            rows={3}
            label="Description"
            value={formData.description}
            onChange={(e) => handleChange('description', e.target.value)}
            placeholder="Describe your hotel..."
          />
        </Grid>

        <Grid item xs={12}>
          <TextField
            fullWidth
            label="Address"
            value={formData.address}
            onChange={(e) => handleChange('address', e.target.value)}
            placeholder="Full address of your hotel"
          />
        </Grid>

        <Grid item xs={12} md={4}>
          <TextField
            fullWidth
            label="Phone Number"
            value={formData.contactInfo.phone}
            onChange={(e) => handleChange('contactInfo.phone', e.target.value)}
            placeholder="+1 (555) 123-4567"
          />
        </Grid>

        <Grid item xs={12} md={4}>
          <TextField
            fullWidth
            label="Email"
            type="email"
            value={formData.contactInfo.email}
            onChange={(e) => handleChange('contactInfo.email', e.target.value)}
            placeholder="contact@yourhotel.com"
          />
        </Grid>

        <Grid item xs={12} md={4}>
          <TextField
            fullWidth
            label="Website"
            value={formData.contactInfo.website}
            onChange={(e) => handleChange('contactInfo.website', e.target.value)}
            placeholder="https://www.yourhotel.com"
          />
        </Grid>
      </Grid>
    </Box>
  );
};

export default BasicInfoStep;
