import React, { useState, useCallback } from 'react';
import {
  Box,
  Stepper,
  Step,
  StepLabel,
  Button,
  Typography,
  Paper,
  Container,
  Alert,
  CircularProgress,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogContentText,
  DialogActions,
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { useMutation } from '@tanstack/react-query';
import { hotelConfigService } from '../../services/hotelConfigService';
import type { 
  HotelConfigFormData, 
  HotelFeature, 
  RoomType, 
  Floor, 
  RoomTemplate 
} from '../../types/hotel';

// Import step components
import BasicInfoStep from './steps/BasicInfoStep';
import FeaturesStep from './steps/FeaturesStep';
import RoomTypesStep from './steps/RoomTypesStep';
import FloorsStep from './steps/FloorsStep';
import RoomTemplatesStep from './steps/RoomTemplatesStep';
import SettingsStep from './steps/SettingsStep';

const steps = [
  'Basic Information',
  'Features & Amenities',
  'Room Types',
  'Floors',
  'Room Templates',
  'Settings',
];

const HotelConfigWizard: React.FC = () => {
  const navigate = useNavigate();
  const [activeStep, setActiveStep] = useState(0);
  const [showExitDialog, setShowExitDialog] = useState(false);
  const [formData, setFormData] = useState<Partial<HotelConfigFormData>>({
    features: [],
    roomTypes: [],
    floors: [],
    roomTemplates: [],
    settings: {
      roomNumberingFormat: 'numeric' as const,
      defaultStatus: 'available' as const,
      currency: 'USD',
      timezone: 'UTC',
      checkInTime: '15:00',
      checkOutTime: '11:00',
    },
  });

  const createConfigMutation = useMutation({
    mutationFn: (data: HotelConfigFormData) => hotelConfigService.createConfig(data),
    onSuccess: () => {
      navigate('/dashboard');
    },
  });

  const handleNext = useCallback(() => {
    if (activeStep === steps.length - 1) {
      handleSubmit();
    } else {
      setActiveStep((prevStep) => prevStep + 1);
    }
  }, [activeStep]);

  const handleBack = useCallback(() => {
    setActiveStep((prevStep) => prevStep - 1);
  }, []);

  const handleStepComplete = useCallback((stepData: Partial<HotelConfigFormData>) => {
    setFormData((prev) => ({ ...prev, ...stepData }));
  }, []);

  const validateStep = useCallback((step: number): boolean => {
    switch (step) {
      case 0: // Basic Info
        return !!formData.name;
      case 1: // Features
        return Array.isArray(formData.features) && formData.features.length > 0;
      case 2: // Room Types
        return Array.isArray(formData.roomTypes) && formData.roomTypes.length > 0;
      case 3: // Floors
        return Array.isArray(formData.floors) && formData.floors.length > 0;
      case 4: // Room Templates
        return Array.isArray(formData.roomTemplates) && formData.roomTemplates.length > 0;
      case 5: // Settings
        return !!formData.settings;
      default:
        return false;
    }
  }, [formData]);

  const handleSubmit = useCallback(() => {
    if (!validateStep(activeStep)) {
      return;
    }
    createConfigMutation.mutate(formData as HotelConfigFormData);
  }, [activeStep, formData, validateStep, createConfigMutation]);

  const handleExit = useCallback(() => {
    setShowExitDialog(true);
  }, []);

  const handleConfirmExit = useCallback(() => {
    navigate('/dashboard');
  }, [navigate]);

  const renderStepContent = useCallback((step: number) => {
    switch (step) {
      case 0:
        return (
          <BasicInfoStep
            initialData={formData}
            onComplete={handleStepComplete}
          />
        );
      case 1:
        return (
          <FeaturesStep
            initialData={formData.features || []}
            onComplete={(features: Omit<HotelFeature, 'id'>[]) => 
              handleStepComplete({ features })}
          />
        );
      case 2:
        return (
          <RoomTypesStep
            initialData={formData.roomTypes || []}
            features={formData.features || []}
            onComplete={(roomTypes: Omit<RoomType, 'id'>[]) => 
              handleStepComplete({ roomTypes })}
          />
        );
      case 3:
        return (
          <FloorsStep
            initialData={formData.floors || []}
            onComplete={(floors: Omit<Floor, 'id'>[]) => 
              handleStepComplete({ floors })}
          />
        );
      case 4:
        return (
          <RoomTemplatesStep
            initialData={formData.roomTemplates || []}
            roomTypes={formData.roomTypes || []}
            floors={formData.floors || []}
            features={formData.features || []}
            onComplete={(roomTemplates: Omit<RoomTemplate, 'id'>[]) => 
              handleStepComplete({ roomTemplates })}
          />
        );
      case 5:
        return (
          <SettingsStep
            initialData={formData.settings || {
              roomNumberingFormat: 'numeric',
              defaultStatus: 'available',
              currency: 'USD',
              timezone: 'UTC',
              checkInTime: '15:00',
              checkOutTime: '11:00',
            }}
            onComplete={(settings: HotelConfigFormData['settings']) => 
              handleStepComplete({ settings })}
          />
        );
      default:
        return null;
    }
  }, [formData, handleStepComplete]);

  return (
    <Container maxWidth="lg">
      <Paper sx={{ p: 4, mt: 4 }}>
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
          <Typography variant="h4">
            Hotel Configuration
          </Typography>
          <Button
            variant="outlined"
            color="error"
            onClick={handleExit}
          >
            Exit Setup
          </Button>
        </Box>
        
        <Typography variant="subtitle1" color="text.secondary" gutterBottom>
          Set up your hotel's configuration to get started with room management
        </Typography>

        <Stepper activeStep={activeStep} sx={{ my: 4 }}>
          {steps.map((label, index) => (
            <Step key={label} completed={validateStep(index)}>
              <StepLabel>{label}</StepLabel>
            </Step>
          ))}
        </Stepper>

        {createConfigMutation.isError && (
          <Alert severity="error" sx={{ mb: 2 }}>
            Error creating hotel configuration. Please try again.
          </Alert>
        )}

        <Box sx={{ mt: 2, position: 'relative' }}>
          {createConfigMutation.isPending && (
            <Box
              sx={{
                position: 'absolute',
                top: 0,
                left: 0,
                right: 0,
                bottom: 0,
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                bgcolor: 'rgba(255, 255, 255, 0.7)',
                zIndex: 1,
              }}
            >
              <CircularProgress />
            </Box>
          )}
          {renderStepContent(activeStep)}
        </Box>

        <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 3 }}>
          <Button
            disabled={activeStep === 0 || createConfigMutation.isPending}
            onClick={handleBack}
          >
            Back
          </Button>
          <Button
            variant="contained"
            onClick={handleNext}
            disabled={!validateStep(activeStep) || createConfigMutation.isPending}
          >
            {activeStep === steps.length - 1 ? 'Create Configuration' : 'Next'}
          </Button>
        </Box>
      </Paper>

      <Dialog
        open={showExitDialog}
        onClose={() => setShowExitDialog(false)}
      >
        <DialogTitle>Exit Configuration Setup?</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Are you sure you want to exit? Any unsaved changes will be lost.
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setShowExitDialog(false)}>
            Cancel
          </Button>
          <Button onClick={handleConfirmExit} color="error" variant="contained">
            Exit
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default HotelConfigWizard;
