import React, { useState, useEffect, useMemo } from 'react';
import {
  Box,
  Grid,
  Paper,
  Typography,
  Card,
  CardContent,
  CardActions,
  Button,
  Chip,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Alert,
  CircularProgress,
  ToggleButtonGroup,
  ToggleButton,
} from '@mui/material';
import {
  Edit as EditIcon,
  Delete as DeleteIcon,
  Add as AddIcon,
  Hotel as HotelIcon,
  Wifi as WifiIcon,
  LocalBar as MinibarIcon,
  Balcony as BalconyIcon,
  Visibility as ViewIcon,
} from '@mui/icons-material';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { hotelConfigService } from '../../services/hotelConfigService';
import type { RoomTemplate, RoomType, Floor, HotelFeature } from '../../types/hotel';
import { RoomStatus, RoomFilter } from '../../types/room';
import RoomFilters from './RoomFilters';
import RoomGrid from './RoomGrid';
import { useRooms } from '../../services/roomService';
import type { Room } from '../../types/room';

const RoomManagement: React.FC = () => {
  const queryClient = useQueryClient();
  const [selectedRoom, setSelectedRoom] = useState<Room | null>(null);
  const [filter, setFilter] = useState<RoomFilter>({
    status: [],
    type: [],
    floor: [],
    features: [],
  });
  const [showRoomDialog, setShowRoomDialog] = useState(false);
  const [dialogMode, setDialogMode] = useState<'edit' | 'view'>('view');
  const [view, setView] = useState<'grid' | 'map'>('grid');
  const [filterStatus, setFilterStatus] = useState<string>('');
  const [filterType, setFilterType] = useState<string>('');
  const [filterFloor, setFilterFloor] = useState<string>('');
  const [search, setSearch] = useState('');
  const [bulkDialogOpen, setBulkDialogOpen] = useState(false);

  const { data: hotelConfig, isLoading } = useQuery({
    queryKey: ['hotelConfig'],
    queryFn: hotelConfigService.getCurrentConfig,
  });

  const { data: rooms = [] } = useRooms();

  const filteredRooms = useMemo(() => {
    return rooms.filter(room =>
      (!filterStatus || room.status === filterStatus) &&
      (!filterType || room.typeId === filterType) &&
      (!filterFloor || room.floorId === filterFloor) &&
      (!search || room.number.includes(search))
    );
  }, [rooms, filterStatus, filterType, filterFloor, search]);

  if (isLoading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="80vh">
        <CircularProgress />
      </Box>
    );
  }

  if (!hotelConfig) {
    return (
      <Box p={3}>
        <Alert severity="warning">
          Hotel configuration not found. Please configure your hotel first.
        </Alert>
      </Box>
    );
  }

  return (
    <Box p={3}>
      <Grid container spacing={3}>
        <Grid item xs={12}>
          <Box display="flex" justifyContent="space-between" alignItems="center">
            <Typography variant="h4">Room Management</Typography>
            <Box>
              <ToggleButtonGroup value={view} exclusive onChange={(_, v) => v && setView(v)} size="small">
                <ToggleButton value="grid">Grid View</ToggleButton>
                <ToggleButton value="map">Map View</ToggleButton>
              </ToggleButtonGroup>
              <Button variant="contained" sx={{ ml: 2 }} onClick={() => setBulkDialogOpen(true)}>
                Bulk Create Rooms
              </Button>
            </Box>
          </Box>
        </Grid>
        <Grid item xs={12}>
          <Paper sx={{ p: 2 }}>
            <Box display="flex" gap={2} mb={2}>
              <FormControl size="small" sx={{ minWidth: 120 }}>
                <InputLabel>Status</InputLabel>
                <Select value={filterStatus} label="Status" onChange={e => setFilterStatus(e.target.value)}>
                  <MenuItem value="">All</MenuItem>
                  <MenuItem value="available">Available</MenuItem>
                  <MenuItem value="occupied">Occupied</MenuItem>
                  <MenuItem value="maintenance">Maintenance</MenuItem>
                  <MenuItem value="cleaning">Cleaning</MenuItem>
                  <MenuItem value="reserved">Reserved</MenuItem>
                </Select>
              </FormControl>
              <FormControl size="small" sx={{ minWidth: 120 }}>
                <InputLabel>Type</InputLabel>
                <Select value={filterType} label="Type" onChange={e => setFilterType(e.target.value)}>
                  <MenuItem value="">All</MenuItem>
                  {hotelConfig.roomTypes.map(rt => (
                    <MenuItem key={rt.id} value={rt.id}>{rt.name}</MenuItem>
                  ))}
                </Select>
              </FormControl>
              <FormControl size="small" sx={{ minWidth: 120 }}>
                <InputLabel>Floor</InputLabel>
                <Select value={filterFloor} label="Floor" onChange={e => setFilterFloor(e.target.value)}>
                  <MenuItem value="">All</MenuItem>
                  {hotelConfig.floors.map(f => (
                    <MenuItem key={f.id} value={f.id}>{f.name} (Floor {f.number})</MenuItem>
                  ))}
                </Select>
              </FormControl>
              <TextField size="small" label="Search" value={search} onChange={e => setSearch(e.target.value)} />
            </Box>
          </Paper>
        </Grid>
        <Grid item xs={12}>
          {view === 'grid' ? (
            <RoomGrid rooms={filteredRooms} roomTypes={hotelConfig.roomTypes} floors={hotelConfig.floors} />
          ) : (
            <Box>
              {hotelConfig.floors.map(floor => (
                <Box key={floor.id} mb={3}>
                  <Typography variant="h6">Floor {floor.number} - {floor.name}</Typography>
                  <Grid container spacing={2}>
                    {filteredRooms.filter(r => r.floorId === floor.id).map(room => (
                      <Grid item xs={12} sm={6} md={4} lg={3} key={room.id}>
                        <RoomGrid rooms={[room]} roomTypes={hotelConfig.roomTypes} floors={hotelConfig.floors} />
                      </Grid>
                    ))}
                  </Grid>
                </Box>
              ))}
            </Box>
          )}
        </Grid>
      </Grid>
      <Dialog open={bulkDialogOpen} onClose={() => setBulkDialogOpen(false)}>
        <DialogTitle>Bulk Create Rooms</DialogTitle>
        <DialogContent>
          <Typography>Bulk creation form coming soon...</Typography>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setBulkDialogOpen(false)}>Cancel</Button>
          <Button variant="contained" disabled>Bulk Create</Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default RoomManagement;
