import React, { useState } from 'react';
import {
  Box,
  Typography,
  Grid,
  TextField,
  Button,
  IconButton,
  Paper,
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
  Chip,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  FormHelperText,
} from '@mui/material';
import { Add as AddIcon, Edit as EditIcon, Delete as DeleteIcon } from '@mui/icons-material';
import type { HotelFeature } from '../../../types/hotel';

interface FeaturesStepProps {
  initialData: Omit<HotelFeature, 'id'>[];
  onComplete: (features: Omit<HotelFeature, 'id'>[]) => void;
}

interface FeatureFormData {
  name: string;
  description: string;
  icon: string;
  type: 'feature' | 'amenity';
  category: 'room' | 'common' | 'service';
}

const FeaturesStep: React.FC<FeaturesStepProps> = ({ initialData, onComplete }) => {
  const [features, setFeatures] = useState<Omit<HotelFeature, 'id'>[]>(initialData);
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [editingIndex, setEditingIndex] = useState<number | null>(null);
  const [formData, setFormData] = useState<FeatureFormData>({
    name: '',
    description: '',
    icon: '',
    type: 'feature',
    category: 'common'
  });

  const handleOpenDialog = (index?: number) => {
    if (typeof index === 'number') {
      const feature = features[index];
      setEditingIndex(index);
      setFormData({
        name: feature.name,
        description: feature.description || '',
        icon: feature.icon || '',
        type: feature.type,
        category: feature.category || 'common'
      });
    } else {
      setEditingIndex(null);
      setFormData({
        name: '',
        description: '',
        icon: '',
        type: 'feature',
        category: 'common'
      });
    }
    setIsDialogOpen(true);
  };

  const handleCloseDialog = () => {
    setIsDialogOpen(false);
    setEditingIndex(null);
    setFormData({ name: '', description: '', icon: '', type: 'feature', category: 'common' });
  };

  const handleSaveFeature = () => {
    if (!formData.name) return;

    const newFeature: Omit<HotelFeature, 'id'> = {
      name: formData.name,
      description: formData.description || undefined,
      icon: formData.icon || undefined,
      type: formData.type,
      category: formData.category
    };

    if (typeof editingIndex === 'number') {
      const newFeatures = [...features];
      newFeatures[editingIndex] = newFeature;
      setFeatures(newFeatures);
    } else {
      setFeatures([...features, newFeature]);
    }

    handleCloseDialog();
  };

  const handleDeleteFeature = (index: number) => {
    const newFeatures = features.filter((_, i) => i !== index);
    setFeatures(newFeatures);
  };

  const handleComplete = () => {
    onComplete(features);
  };

  return (
    <Box>
      <Typography variant="h6" gutterBottom>
        Features & Amenities
      </Typography>
      <Typography variant="body2" color="text.secondary" paragraph>
        Define the features and amenities available in your hotel. These can be used to describe rooms and common areas.
      </Typography>

      <Box sx={{ mb: 3 }}>
        <Button
          variant="contained"
          startIcon={<AddIcon />}
          onClick={() => handleOpenDialog()}
        >
          Add Feature
        </Button>
      </Box>

      <Grid container spacing={2}>
        <Grid item xs={12}>
          <Paper>
            <List>
              {features.map((feature, index) => (
                <ListItem key={index}>
                  <ListItemText
                    primary={feature.name}
                    secondary={feature.description}
                  />
                  <ListItemSecondaryAction>
                    <IconButton
                      edge="end"
                      aria-label="edit"
                      onClick={() => handleOpenDialog(index)}
                      sx={{ mr: 1 }}
                    >
                      <EditIcon />
                    </IconButton>
                    <IconButton
                      edge="end"
                      aria-label="delete"
                      onClick={() => handleDeleteFeature(index)}
                    >
                      <DeleteIcon />
                    </IconButton>
                  </ListItemSecondaryAction>
                </ListItem>
              ))}
              {features.length === 0 && (
                <ListItem>
                  <ListItemText
                    primary="No features added yet"
                    secondary="Click 'Add Feature' to get started"
                  />
                </ListItem>
              )}
            </List>
          </Paper>
        </Grid>
      </Grid>

      <Dialog open={isDialogOpen} onClose={handleCloseDialog} maxWidth="sm" fullWidth>
        <DialogTitle>
          {editingIndex !== null ? 'Edit Feature' : 'Add Feature'}
        </DialogTitle>
        <DialogContent>
          <Box sx={{ pt: 2 }}>
            <TextField
              fullWidth
              label="Name"
              value={formData.name}
              onChange={(e) => setFormData({ ...formData, name: e.target.value })}
              margin="normal"
              required
            />
            <TextField
              fullWidth
              label="Description"
              value={formData.description}
              onChange={(e) => setFormData({ ...formData, description: e.target.value })}
              margin="normal"
              multiline
              rows={2}
            />
            <TextField
              fullWidth
              label="Icon (Material Icon name)"
              value={formData.icon}
              onChange={(e) => setFormData({ ...formData, icon: e.target.value })}
              margin="normal"
              helperText="Enter a Material Icon name (e.g., wifi, pool, spa)"
            />
            <FormControl fullWidth margin="normal">
              <InputLabel>Type</InputLabel>
              <Select
                value={formData.type}
                onChange={(e) => setFormData({ ...formData, type: e.target.value as 'feature' | 'amenity' })}
                label="Type"
              >
                <MenuItem value="feature">Feature (Structural)</MenuItem>
                <MenuItem value="amenity">Amenity (Service/Item)</MenuItem>
              </Select>
              <FormHelperText>
                Features are structural elements, while amenities are provided services or items
              </FormHelperText>
            </FormControl>
            <FormControl fullWidth margin="normal">
              <InputLabel>Category</InputLabel>
              <Select
                value={formData.category}
                onChange={(e) => setFormData({ ...formData, category: e.target.value as 'room' | 'common' | 'service' })}
                label="Category"
              >
                <MenuItem value="room">Room-specific</MenuItem>
                <MenuItem value="common">Common Area</MenuItem>
                <MenuItem value="service">Service</MenuItem>
              </Select>
              <FormHelperText>
                Where this feature or amenity is available
              </FormHelperText>
            </FormControl>
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDialog}>Cancel</Button>
          <Button
            onClick={handleSaveFeature}
            variant="contained"
            disabled={!formData.name}
          >
            {editingIndex !== null ? 'Save Changes' : 'Add Feature'}
          </Button>
        </DialogActions>
      </Dialog>

      <Box sx={{ mt: 4, display: 'flex', justifyContent: 'flex-end' }}>
        <Button
          variant="contained"
          onClick={handleComplete}
          disabled={features.length === 0}
        >
          Continue
        </Button>
      </Box>
    </Box>
  );
};

export default FeaturesStep;
